        </div>
        <div className="logs-item">
          <label htmlFor="time">时长</label>
          <input id="time" type="number" onChange={timeChange} />
        </div>
        <div className="logs-btn">
          <button>添加</button>
        </div>
      </form>
    </Card>
  );
};

